import * as THREE from "three";
import alphaTexture from "../../../assets/textures/stripes_gradient.jpg";

import * as OBJLoader from "three-obj-loader";
import model from "../../../assets/models/dummy.obj";


OBJLoader(THREE);

this.loader = new THREE.OBJLoader();


const loadObj = () => {
  this.loader.load(
    // resource URL
    model,
    // called when resource is loaded
    object => {
      console.log(object);
      this.setState({ resource: object });
    },
    // called when loading is in progresses
    xhr => {
      console.log((xhr.loaded / xhr.total) * 100 + "% loaded");
    },
    // called when loading has errors
    error => {
      console.log("An error happened");
    }
  );
};

export default (scene, geo, model) => {
  const group = new THREE.Group();

  // const subjectGeometry = deformGeometry(geo);
  const subjectGeometry = geo;
  const subjectMaterial = new THREE.MeshStandardMaterial({
    color: "#000",
    transparent: true,
    side: THREE.DoubleSide,
    alphaTest: 0.5
  });
  subjectMaterial.alphaMap = new THREE.TextureLoader().load(alphaTexture);
  subjectMaterial.alphaMap.magFilter = THREE.NearestFilter;
  subjectMaterial.alphaMap.wrapT = THREE.RepeatWrapping;
  subjectMaterial.alphaMap.repeat.y = 1;

  const subjectMesh = new THREE.Mesh(subjectGeometry, subjectMaterial);

  const subjectWireframe = new THREE.LineSegments(
    new THREE.EdgesGeometry(subjectGeometry),
    new THREE.LineBasicMaterial()
  );

  //upload a model

  group.add(subjectMesh);
  group.add(subjectWireframe);
  scene.add(group);

  group.rotation.z = 6 * Math.PI;

  const speed = 0.02;
  const textureOffsetSpeed = 0.02;

  // function deformGeometry(geometry) {
  //     for (let i=0; i<geometry.vertices.length; i+=2) {
  //         const scalar = 1 + Math.random()*0.8;
  //         geometry.vertices[i].multiplyScalar(scalar)
  //     }

  //     return geometry;
  // }

  function update(time) {
    const angle = time * speed;

    group.rotation.y = angle;

    subjectMaterial.alphaMap.offset.y = 0.55 + time * textureOffsetSpeed;

    subjectWireframe.material.color.setHSL(Math.sin(angle * 2), 0.5, 0.5);

    const scale = (Math.sin(angle * 8) + 6.4) / 5;
    subjectWireframe.scale.set(scale, scale, scale);
  }

  return {
    update
  };
};
