import React, { Component } from "react";
import * as THREE from "three";
import * as OBJLoader from "three-obj-loader";

import model0 from "../../assets/models/dummy.obj";
import model1 from "../../assets/models/cube.obj";

//putting models in an array
const models = [];
models.push(model0);
models.push(model1);

OBJLoader(THREE);

class CanvasObj extends Component {
  componentDidMount() {
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;
    //ADD SCENE
    this.scene = new THREE.Scene();
    //ADD CAMERA
    this.camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    this.camera.position.z = 4;

    this.scene.add(this.camera);
    //ADD LIGHT
    this.light = new THREE.DirectionalLight(0xffffff);
    this.light.position.z = 4;
    this.light.position.x = 4;

    this.scene.add(this.light);
    //load models
    this.THREE = THREE;
    this.loader = new this.THREE.OBJLoader();

    this.loadGeo();

    //ADD RENDERER
    this.renderer = new THREE.WebGLRenderer({ antialias: true });
    this.renderer.setClearColor("#000000");
    this.renderer.setSize(width, height);
    this.mount.appendChild(this.renderer.domElement);
    //ADD CUBE
    const geometry = new THREE.BoxGeometry(1.3, 1.3, 1.3);
    const material = new THREE.MeshLambertMaterial({
      color: "#88d200",
      wireframe: false
    });
    this.cube = new THREE.Mesh(geometry, material);
    // this.scene.add(this.cube);
    this.start();
  }

  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);
  }

  start = () => {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  };

  stop = () => {
    cancelAnimationFrame(this.frameId);
  };

  loadGeo = () => {
    if (this.props.model === "Dummy") {
      this.loader.load(
        // resource URL
        models[0],
        // called when resource is loaded
        object => {
          console.log(object, this.props.model);
          object.position.set(0, -0.5, 0);
          object.scale.set(0.01, 0.01, 0.01);
          this.scene.add(object);
        },
        // called when loading is in progresses
        xhr => {
          console.log((xhr.loaded / xhr.total) * 100 + "% loaded");
        },
        // called when loading has errors
        error => {
          console.log("An error happened");
        }
      );
    }
    else{
      this.loader.load(
        // resource URL
        models[1],
        // called when resource is loaded
        object => {
          console.log(object, this.props.model);
          object.position.set(0, -0.5, 0);
          object.scale.set(0.01, 0.01, 0.01);
          this.scene.add(object);
        },
        // called when loading is in progresses
        xhr => {
          console.log((xhr.loaded / xhr.total) * 100 + "% loaded");
        },
        // called when loading has errors
        error => {
          console.log("An error happened");
        }
      );
    }
  };

  animate = () => {
    this.cube.rotation.x += 0.01;
    this.cube.rotation.y += 0.01;
    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  };
  renderScene = () => {
    this.renderer.render(this.scene, this.camera);
  };
  render() {
    return (
      <div
        style={{ width: "100%", height: "100vh" }}
        ref={mount => {
          this.mount = mount;
        }}
      />
    );
  }
}
export default CanvasObj;
