import React, { Component } from "react";
import * as THREE from "three";

import threeEntryPoint from "./threejs/threeEntryPoint";
import * as OBJLoader from "three-obj-loader";

import model from "../../assets/models/dummy.obj";

OBJLoader(THREE);

class CanvasObj extends Component {
  componentDidMount() {
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;
    //ADD SCENE
    this.scene = new THREE.Scene();
    //ADD CAMERA
    this.camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    this.camera.position.z = 4;

    this.scene.add(this.camera);
    //ADD LIGHT
    this.light = new THREE.DirectionalLight(0xffffff);
    this.light.position.z = 4;
    this.light.position.x = 4;

    this.scene.add(this.light);
    //ADD RENDERER
    this.renderer = new THREE.WebGLRenderer({ antialias: true });
    this.renderer.setClearColor("#000000");
    this.renderer.setSize(width, height);
    this.mount.appendChild(this.renderer.domElement);
    //ADD CUBE
    const geometry = new THREE.BoxGeometry(3, 3, 3);
    const material = new THREE.MeshLambertMaterial({ color: "#433F81", wireframe:false });
    this.cube = new THREE.Mesh(geometry, material);
    this.scene.add(this.cube);
    this.start();
  }

  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);
  }

  start = () => {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  };

  stop = () => {
    cancelAnimationFrame(this.frameId);
  };

  animate = () => {
    this.cube.rotation.x += 0.01;
    this.cube.rotation.y += 0.01;
    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  };
  renderScene = () => {
    this.renderer.render(this.scene, this.camera);
  };
  render() {
    return (
      <div
        style={{ width: "80%", height: "100vh" }}
        ref={mount => {
          this.mount = mount;
        }}
      />
    );
  }
}
export default CanvasObj;
